/*
import logo from './logo.svg';
import './App.css';
import Button from '@material-ui/core/Button'
*/

/*
  <div className="App">
    <header className="App-header">
      <h1>Projeto Karangos</h1>
      <Button variant="contained" color="primary">Clique aqui!</Button>
      <img src={logo} className="App-logo" alt="logo" />
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
    </header>
  </div>
*/
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import yellow from '@material-ui/core/colors/yellow';
import pink from '@material-ui/core/colors/red';
import TopBar from './ui/TopBar'
import FooterBar from './ui/FooterBar'
import Box from '@material-ui/core/Box'
import { makeStyles } from '@material-ui/core/styles'
import { BrowserRouter, Route, Switch } from 'react-router-dom'
import KarangosList from'./routed/KarangosList'
import KarangosForm from'./routed/KarangosForm'

const theme = createMuiTheme({
  palette: {
    type: 'dark',
    primary: {
      main: yellow[500],
    },
    secondary: {
      main: red[500],
    },
  },
});

const useStyles = makeStyles((theme) => ({
  main: {
    backgroundColor: theme.palette.background.default,
    minHeight: '100vh', // 100% da altura da área de visualização
    margin: '0 0 42px 0'  // margem de 42px inferior para acomodar a barra inferior
  },

  routed: {
    padding: '25px',
    color: theme.palette.text.primary,
    fontFamilly:theme.typography.fontfamily
  }
}))

function Main() {
  const classes = useStyles()
  return (
    <Box className={classes.main}>
      <BrowserRouter>{/* Inicia a região onde pode haver troca dinâmica de elementos*/}
        <TopBar />
        <Box id="routed" classname={classes.routed}>
          <Switch> {/* Determina qual elemento será exibido, de acordo com a rota */}

            <Route path="/list">
              <KarangosList/>
            </Route>

            <Route path="/new">
              <KarangosForm/>
            </Route>

          </Switch>
        </Box>
        <FooterBar/>
      </BrowserRouter>
    </Box>
  )
}

function App() {
  return (
    <ThemeProvider theme={theme}>
      <Main />
    </ThemeProvider>
  );
}

export default App;
